data <- data.frame(Age = age, Profit = profit)

ggplot(data, aes(x = Age, y = Profit)) +
  geom_point(color = "blue", size = 2) +
  labs(title = "Scatter Plot: Age vs Vehicle Profit",
       x = "Age of Buyer",
       y = "Profit on Vehicle Sale") 

cor_coeff <- cor(data$Age, data$Profit)
cat("Correlation Coefficient:", round(cor_coeff,3))

if (cor_coeff > 0) {
  cat("There is a positive correlation, meaning that as the buyer's age increases, the profit on vehicle sales tends to increase.")
} else if (cor_coeff < 0) {
  cat("There is a negative correlation, meaning that as the buyer's age increases, the profit on vehicle sales tends to decrease.")
} else {
  cat("There is no correlation between buyer's age and vehicle profit.")
}
